import   React ,{useEffect, useState} from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import "bootstrap/dist/js/bootstrap.bundle.min";
import axios from "axios";
function App(){
const [title,settitle]=useState('');
const [file,setfile]=useState("");
const [allImage,setallImage]=useState(null);

useEffect(()=>{
    getpdf();
},[])
const getpdf=async()=>{
    const result=await axios.get('http://localhost:5000/get-files')
    console.log(result.data.data);
    setallImage(result.data.data);
}

const submitImage=async(e)=>{
    e.preventDefault();
    const formData=new FormData();
    formData.append('title',title);
    formData.append('file',file);

    console.log(file+title);
    const result=await axios.post('http://localhost:5000/upload-files',
    formData,
    {
        headers:{'content-Type':'multipart/form-data'},
    })
    console.log(result);
}

const showpdf=(pdf)=>{
    
}

return(
   <div className="all">
     <div className="App">
        <form  className="formstyle" onSubmit={submitImage}>
            <h4>Upload pdf in react </h4>
            <br />
            <input type="text" onChange={(e)=>{settitle(e.target.value)}} placeholder="Title" className="form-control" required/>
            <input type="file" onChange={(e)=>{setfile(e.target.files[0])}} className="form-control"  accept="application/pdf" required/>
            <button className="btn btn-primary mt-3">Submit</button>
        </form>

    </div>
    <div className="card">
        <div className="uploaded">
            <h4>uploaded pdf</h4>
            <div className="ouput-div">
                {
                    allImage===null?"":allImage.map((data)=>{
                        return(
                            <div className="inner-div">
                            <h6>Title:   {data.title}</h6>
                            <button className="btn btn-success" onClick={()=>{showpdf(data.pdf)}}>show pdf</button>
                        </div>
                        )
                    })
                }
               
            </div>
        </div>
    </div>



   </div>
    
)
}
export default App;
