import React from 'react'
import ReactDOM  from 'react-dom'
import "./index.css"
import books from './data'




const allbook=books.map((book,index)=>{
    const {image,title,auther,children}=book;

    return <section className='book'>
        <img src={image}alt='img' />
        <h2> {title}</h2>
        <h3><span style={{color:"black"}}>auth :</span>{auther}</h3>
        {children}
    </section>
})

ReactDOM.render(<div className='booklist'>
{allbook}
    </div>,document.getElementById('root'))
